package com.orange.groupbuy.manager;import javax.xml.bind.annotation.adapters.XmlJavaTypeAdapter.DEFAULT;public class CommonManager {		private static final int DEFAULT_OFFSET = 0;	private static int LIMIT_MAXCOUNT = 20;		private static int getNumber (String number, int defaultNumber) {		if (number == null || number.trim().length() < 1) {			return defaultNumber;		}		return Integer.parseInt(number);	}		protected static int getMaxcount(String maxCount) {		return getNumber(maxCount, LIMIT_MAXCOUNT);	}		protected static int getOffset(String offset) {		return getNumber(offset, DEFAULT_OFFSET);	}}