package com.orange.groupbuy.manager;public class CommonManager {		private static final int DEFAULT_OFFSET = 0;	private static final double DEFAULT_LATITUDE = 22.0;	private static final double DEFAULT_LONGITUDE = 113.0;	private static final double DEFAULT_RADIUS = 20;		private static int LIMIT_MAXCOUNT = 20;		private static int getInt (String number, int defaultNumber) {		if (number == null || number.trim().length() < 1) {			return defaultNumber;		}		return Integer.parseInt(number);	}		private static double getDouble(String number, double defaultNumber) {		if (number == null || number.trim().length() < 1) {			return defaultNumber;		}		return Double.parseDouble(number);	}	protected static int getMaxcount(String maxCount) {		return getInt(maxCount, LIMIT_MAXCOUNT);	}		protected static int getOffset(String offset) {		return getInt(offset, DEFAULT_OFFSET);	}		protected static double getLatitude(String latitude) {		return getDouble(latitude,DEFAULT_LATITUDE);	}	protected static double getLongitude(String longitude) {		return getDouble(longitude, DEFAULT_LONGITUDE);	}		protected static double getRadius(String radius) {		return getDouble(radius, DEFAULT_RADIUS);			}}